(()=>{"use strict";const e=async()=>{const e=await(async()=>{try{const e=await fetch("https://api.adviceslip.com/advice");return await e.json()}catch(e){console.log("Fetch Error",e)}})();return`\n        <h1 class="advice_number">ADVICE #${e.slip.id}</h1>\n        <p class="advice_content">"${e.slip.advice}"</p>\n        <div class="image_sep"></div>\n    `};window.addEventListener("load",(async function(){document.querySelector(".main").innerHTML=await e()})),document.getElementById("reload").addEventListener("click",(e=>{location.reload()}))})();