(()=>{"use strict";const n=async()=>{const n=await(async()=>{try{const n=await fetch("https://api.adviceslip.com/advice");return await n.json()}catch(n){console.log("Fetch Error",n)}})();return`\n        <h1 class="advice_number">ADVICE # ${n.slip.id}</h1>\n        <p class="advice_content">"${n.slip.advice}"</p>\n        \n        \n    `};window.addEventListener("load",(async function(){document.querySelector(".main").innerHTML=await n()})),document.getElementById("reload").addEventListener("click",(n=>{location.reload()}))})();