(()=>{"use strict";const n=async()=>{const n=await(async()=>{try{const n=await fetch("https://api.adviceslip.com/advice");return await n.json()}catch(n){console.log("Fetch Error",n)}})();return`\n        <h1 class="advice_number">ADVICE # ${n.slip.id}</h1>\n        <p class="advice_content">"${n.slip.advice}"</p>\n        <div class="image_sep"></div>\n        <div class="image_dado-content">\n            <div class="image_dado"></div>\n        </div>\n        \n    `};window.addEventListener("load",(async function(){document.querySelector(".main").innerHTML=await n()}))})();